mapper.select:
select

from user
where 1=1

    and id = #{id,jdbcType=VARCHAR}


    and phone = #{phone,jdbcType=VARCHAR}


    and username = #{username,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}


    and gender = #{gender,jdbcType=CHAR}


    and trueName = #{truename,jdbcType=VARCHAR}


    and birthday = #{birthday,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and personalBrief = #{personalbrief,jdbcType=VARCHAR}


    and recentlyLanded = #{recentlylanded,jdbcType=VARCHAR}


    and avatarImgUrl = #{avatarimgurl,jdbcType=LONGVARCHAR}
